import React, { useState, useEffect } from 'react';
import axios from "axios";
import { useNavigate } from "react-router-dom";
import FinanceFilters from '../components/FinanceFilters';
import FinanceForm from '../components/FinanceForm';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
import NavScrollExample from "../components/Navbar";
import { Link } from "react-router-dom";

const Finances = () => {
    const navigate = useNavigate();
    const [modal, setModal] = useState(false);
    const [finances, setFinances] = useState([]);
    const [categories, setCategories] = useState([]);
    const [paymentMethods, setPaymentMethods] = useState([]);
    const [formData, setFormData] = useState({
        amount: "",
        category: "",
        payment_method: "",
        type: "Expense",
        note: "",
        username: localStorage.getItem("username"),
        create: new Date().toISOString().split('T')[0],
    });
    const [filters, setFilters] = useState({
        category: "",
        payment_method: "",
        start_date: "",
        end_date: ""
    });
    const API_URL = window.location.origin.includes("devtunnels.ms")
                ? "https://250w7qvn-8000.usw3.devtunnels.ms/api/finances/"
                : "http://127.0.0.1:8000/api/finances/";
    const [amountTotal, setAmountTotal] = useState(0);

    const fetchData = async () => {
        const token = localStorage.getItem("token");
        const username = localStorage.getItem("username");
        if (!token || !username) return navigate("/login");
        try {
            const res = await axios.get(API_URL, {
                headers: { Authorization: `Bearer ${token}` },
                params: { ...filters, username: formData.username }
            });
            setFinances(res.data.finances);
            setCategories(res.data.categories || []);
            setPaymentMethods(res.data.payment_methods || []);
            setAmountTotal(res.data.finances.reduce((total, finance) => total + parseFloat(finance.amount), 0));
        } catch (err) {
            if (err.response && err.response.status === 401) {
                navigate("/login");
            } else {
                console.error(err);
            }
        }
    };

    useEffect(() => {        
        fetchData();
    }, [filters, formData.username]);

    const handleFilterChange = (e) => {
        setFilters({
            ...filters,
            [e.target.name]: e.target.value
        });
    };

    const applyFilters = () => {
        fetchFinancesData(filters, formData.username, API_URL);
    };

    const toggle = () => {
        setModal(!modal);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("token");
        if (!token) return navigate("/login");
        try {
            await axios.post(API_URL, formData, {
                headers: { Authorization: `Bearer ${token}` }
            });
            toggle();
            fetchData();
        } catch (err) {
            if (err.response && err.response.status === 401) {
                navigate("/login");
            } else {
                console.error(err);
            }
        }
    };
    
    const username = localStorage.getItem("username");

    return (
        <>
            <NavScrollExample />
            <Button as={Link} to="/dashboard" variant="primary">
                Ir a Dashboard
            </Button>
            <Button as={Link} to="/relationship" variant="primary">
                Ir a Relaciones
            </Button>
            <FinanceFilters
                filters={filters}
                categories={categories}
                paymentMethods={paymentMethods}
                handleFilterChange={handleFilterChange}
                applyFilters={applyFilters}
            />
            <h2>Finanzas {username}</h2>
            <Button color="primary" onClick={toggle}>Agregar Finanzas</Button>
            <h2>Total Finanzas: ${amountTotal}</h2>
            <Table striped className="mt-3">
                <thead>
                    <tr>
                        <th>Fecha</th>
                        <th>Monto</th>
                        <th>Categoría</th>
                        <th>Método de Pago</th>
                        <th>Tipo</th>
                        <th>Nota</th>
                    </tr>
                </thead>
                <tbody>
                    {finances.map((finance) => (
                        <tr key={finance.id}>
                            <td>{finance.create}</td>
                            <td>${finance.amount}</td>
                            <td>{finance.category}</td>
                            <td>{finance.payment_method}</td>
                            <td>{finance.type === "Expense" ? "Gasto" : "Ingreso"}</td>
                            <td>{finance.note}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <FinanceForm
                modal={modal}
                toggle={toggle}
                formData={formData}
                setFormData={setFormData}
                categories={categories}
                paymentMethods={paymentMethods}
                handleSubmit={handleSubmit}
            />
        </>
    );
};

export default Finances;
