import React from "react";
import { Container, Card, CardBody } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

/**
 * Slider infinito con EXACTAMENTE 3 elementos visibles siempre.
 * - Mueve el track de 0% a -50% y repite (duplicamos los 3 para cerrar el bucle)
 * - Cuando el primero sale por la izquierda, reaparece por la derecha (loop perfect).
 *
 * Props:
 *  - items: ReactNode[]  (pásame EXACTAMENTE 3 elementos)
 *  - speed: string       (ej. "12s", "20s"…; por defecto "12s")
 */
export default function Method() {
    const items = [
        <Card className="shadow-sm"><CardBody className="text-center fs-4">Elemento 1</CardBody></Card>,
        <Card className="shadow-sm"><CardBody className="text-center fs-4">Elemento 2</CardBody></Card>,
        <Card className="shadow-sm"><CardBody className="text-center fs-4">Elemento 3</CardBody></Card>,
    ];

    return <InfiniteThreeSlider items={items} speed="17s" />;
}

function InfiniteThreeSlider({ items = [], speed = "12s" }) {
  if (items.length !== 3) {
    console.warn("InfiniteThreeSlider: Debes pasar exactamente 3 elementos.");
  }

  // Duplicamos los 3 para hacer el loop continuo sin cortes.
  const loopItems = [...items, ...items];

  return (
    <Container fluid className="py-4 bg-light">
      <div className="loop" style={{ ["--speed"]: speed }}>
        <div className="track">
          {loopItems.map((node, i) => (
            <div className="slide" key={i}>
              {node}
            </div>
          ))}
        </div>
      </div>

      <style>{`
        .loop {
          width: 100%;
          overflow: hidden;
        }

        /* El track mide 200% (porque duplicamos los 3 = 6 slides).
           Al animar a -50% desplazamos exactamente el ancho del primer set (3 slides). */
        .track {
          display: flex;
          width: 200%;
          animation: scroll var(--speed, 12s) linear infinite;
          will-change: transform;
        }

        .track:hover {
          animation-play-state: paused; /* opcional: pausa al pasar el mouse */
        }

        /* 3 visibles siempre: cada slide ocupa 1/3 del ancho del contenedor */
        .slide {
          flex: 0 0 17%;
          box-sizing: border-box;
          padding: 0.5rem;
        }

        @keyframes scroll {
          from { transform: translateX(0); }
          to   { transform: translateX(-100%); } /* desplaza 3 slides y repite sin salto */
        }

        /* Respeto accesibilidad: si usuario prefiere menos motion */
        @media (prefers-reduced-motion: reduce) {
          .track { animation: none; transform: none; }
        }
      `}</style>
    </Container>
  );
}
