import React from "react";
import { Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const items = [
  { id: 1, content: "Elemento 1" },
  { id: 2, content: "Elemento 2" },
  { id: 3, content: "Elemento 3" },
  { id: 4, content: "Elemento 4" },
  { id: 5, content: "Elemento 5" },
];


export default function Method() {
  return (
    <Container fluid className="overflow-hidden bg-light py-3">
      <div className="slider">
        <div className="slider-track">
          {items.concat(items).map((item, index) => (
            <div className="slide" key={index}>
              {item.content}
            </div>
          ))}
        </div>
      </div>

      <style>{`
        .slider {
          position: relative;
          width: 100%;
          overflow: hidden;
        }

        .slider-track {
          display: flex;
          width: calc(200%); /* Duplicamos la lista */
          animation: scroll 15s linear infinite;
        }

        .slide {
          flex: 0 0 33.333%; /* Siempre 3 visibles */
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1.5rem;
          font-weight: bold;
          background: #f8f9fa;
          border: 1px solid #000000ff;
          padding: 2rem;
          
        }

        @keyframes scroll {
          from {
            transform: translateX(0);
          }
          to {
            transform: translateX(-50%);
          }
        }
      `}</style>
    </Container>
  );
}
