import React, { useEffect, useRef, useState } from "react";
import { Container, Card, CardBody } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

export default function Demo() {
  const items = [
    <Card><CardBody className="text-center fs-4">Elemento 1</CardBody></Card>,
    <Card><CardBody className="text-center fs-4">Elemento 2</CardBody></Card>,
    <Card><CardBody className="text-center fs-4">Elemento 3</CardBody></Card>,
  ];

  return <InfiniteFlowSlider items={items} />;
}

function InfiniteFlowSlider({ items }) {
  const [slides, setSlides] = useState([...items, ...items, ...items]);
  const trackRef = useRef(null);
    
  useEffect(() => {
    const track = trackRef.current;
    let animationFrame;
    let offset = 0;

    const speed = 1; // px por frame ~60px/s aprox a 60fps

    const animate = () => {
      offset -= speed;
      track.style.transform = `translateX(${offset}px)`;

      const firstSlide = track.children[0];
      if (firstSlide && -offset >= firstSlide.offsetWidth) {
        // Mover el primero al final
        track.appendChild(firstSlide);
        offset += firstSlide.offsetWidth;
      }

      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);

    return () => cancelAnimationFrame(animationFrame);
  }, []);

  return (
    <Container fluid className="bg-light py-4 overflow-hidden">
      <div className="slider" style={{ overflow: "hidden", width: "100%" }}>
        <div
          ref={trackRef}
          style={{ display: "flex", willChange: "transform" }}
        >
          {slides.map((node, i) => (
            <div
              key={i}
              style={{
                flex: "0 0 33.333%",
                padding: "0.5rem",
                boxSizing: "border-box",
              }}
            >
              {node}
            </div>
          ))}
        </div>
      </div>
    </Container>
  );
}

