import { useEffect, useState } from "react";
import Container from "react-bootstrap/esm/Container";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import logo from '../assets/img/logo.svg';


export default function WhoWeAre() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8000/api/who-we-are/")
      .then((res) => {
        if (!res.ok) throw new Error("Error al cargar datos");
        console.log("Respuesta del backend:", res);
        return res.json();
      })
      .then((data) => {
        console.log("Respuesta del backend:", data);
        setItems(data.who_we_are || []);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  return (
    <section className="p-3">
        <Container>
            <Row>
                <Col md={6}>
                    <h2>¿Quiénes somos?</h2>
                    <h3 className="my-5">SOBI´S DEV</h3>
                    {loading && <p>Cargando...</p>}
                    {error && <p>Error: {error}</p>}
                    <Card
                        style={{
                            width: '100%',
                            background: "linear-gradient(to bottom, #7C83EF, #474B89)",
                            color: "#fff"
                        }}
                        className="mb-3 p-5"
                    >
                        {items.map((item, idx) => (
                            <h3 key={idx} style={{ whiteSpace: "pre-line" }}>
                            {item.content.replace(/\r\n/g, "\n")}
                            </h3>
                        ))}
                    </Card>
                </Col>
                <Col md={6}>
                    <img
                        src={logo}
                        alt="Logo"
                        width="250"
                        height="250"
                        style={{ borderRadius: "50%", boxShadow: "0 0 10px rgba(0, 0, 0, 0.5)" }}
                    />
                    <br className="d-none d-md-block"/>
                    <div className="d-none d-md-flex justify-content-md-end">
                        <img
                            src={logo}
                            alt="Logo"
                            width="250"
                            height="250"
                            style={{ borderRadius: "50%", boxShadow: "0 0 10px rgba(0, 0, 0, 0.5)" }}
                        />
                    </div>
                    <br className="d-none d-md-block"/>
                    <img
                        src={logo}
                        alt="Logo"
                        width="250"
                        height="250"
                        style={{ borderRadius: "50%", boxShadow: "0 0 10px rgba(0, 0, 0, 0.5)" }}
                    />
                </Col>
            </Row>
        </Container>
    </section>
  );
}
